import { Badge, Col, Row } from 'react-bootstrap'
import { connect } from 'react-redux'
import React from 'react'

import { SET_POKEMONS_FILTER_ITEM, FORCE_POKEMONS_REFRESH } from './pokemons.reducer'
import { sd } from '../../../helpers/redux'
import SelectPokemonsOrder from './SelectPokemonsOrder'

const ListPokemonsSortBar = ({ filter, onChange }) => {
	const { sort, ...restFilter } = filter

	return (
		<Row>
			<Col xs={8}>
				{Object.keys(restFilter).map(key => (
					<Badge pill variant="secondary">
						{key}
					</Badge>
				))}
			</Col>

			<Col xs={4}>
				<div style={{ display: 'flex', alignItems: 'center', justifyContent: 'flex-end' }}>
					<small style={{ marginRight: 6, display: 'block' }}>Ordenação:</small>
					<SelectPokemonsOrder onChange={e => onChange('sort', e.target.value)} value={sort} />
				</div>
			</Col>
		</Row>
	)
}

const mapState = state => ({
	filter: state.pokemons.filter
})

const mapActions = d => ({
	onChange(field, value) {
		sd(d, SET_POKEMONS_FILTER_ITEM, { field, value })
		sd(d, FORCE_POKEMONS_REFRESH)
	}
})

export default connect(mapState, mapActions)(ListPokemonsSortBar)
