import { Button, Col, Row } from 'react-bootstrap'
import { connect } from 'react-redux'
import React from 'react'

import { ADD_POKEMONS_PAGE } from './pokemons.reducer'
import { sd } from '../../../helpers/redux'
import PokemonCard from '../_common/PokemonCard'

const ListPokemons = ({ pokemons, loadMore, isLoadingMore, canLoadMore }) => (
	<div>
		<Row>
			{pokemons.map(pokemon => (
				<Col md={4} key={pokemon.id}>
					<PokemonCard pokemon={pokemon} />
				</Col>
			))}
		</Row>

		{canLoadMore && (
			<Button onClick={!isLoadingMore && loadMore} variant="outline-primary">
				{!isLoadingMore ? 'Ver Mais' : 'Carregando...'}
			</Button>
		)}
	</div>
)

const mapState = state => ({
	pokemons: state.pokemons.list,
	isLoadingMore: state.pokemons.isLoadingMore,
	canLoadMore: state.pokemons.canLoadMore
})

const mapActions = d => ({
	loadMore() {
		sd(d, ADD_POKEMONS_PAGE)
	}
})

export default connect(mapState, mapActions)(ListPokemons)
