import React from 'react'

import Gallery from '../../../components/presentation/Gallery'
import ShowPokemonLoader from './ShowPokemonLoader'
import Text from '../../../components/presentation/Text'
import TitleContent from '../../../components/presentation/TitleContent'

const PokemonMainContent = ({ pokemon, id }) => {
	if (!pokemon.id) return false

	return (
		<div>
			<TitleContent title="Descrição">
				<Text size="p">{pokemon.name}</Text>
			</TitleContent>
