import { Col, Image, Row } from 'react-bootstrap'
import { connect } from 'react-redux'
import { push } from 'react-router-redux'
import React from 'react'

import { SET_POKEMON_SINGLE } from '../../pokemons/show/pokemon.reducer'
import { sd } from '../../../helpers/redux'
import { formatTo3Digits } from '../../../helpers/numbers'
import ShowPokemonFamilyLoader from './ShowPokemonFamilyLoader'
import Text from '../../../components/presentation/Text'
import PokemonCard from '../../pokemons/_common/PokemonCard'

const ShowPokemonFamily = ({ familyID, familyMembers, goTo }) => {
	if (!familyID) return false

	return (
		<ShowPokemonFamilyLoader familyID={familyID}>
			<div style={{ maxWidth: 600, margin: 'auto' }}>
				<Row>
					{familyMembers.map(pokemon => (
						<Col>
							<PokemonCard pokemon={pokemon} />
							<a onClick={() => goTo(pokemon)}>
								<Image
									roundedCircle
									src={`https://assets.pokemon.com/assets/cms2/img/pokedex/detail/${formatTo3Digits(
										pokemon.number
									)}.png`}
								/>
								<Text>{pokemon.name}</Text>
							</a>
						</Col>
					))}
				</Row>
			</div>
		</ShowPokemonFamilyLoader>
	)
}

const mapState = state => ({
	familyMembers: state.pokemonFamily.single.members || []
})

const mapActions = d => ({
	goTo(pokemon) {
		sd(d, SET_POKEMON_SINGLE, pokemon)
		d(push('/pokemons/' + pokemon.id))
	}
})

export default connect(mapState, mapActions)(ShowPokemonFamily)
