const defaultState = {
	list: [],
	filter: {},
	total: 0,
	page: 1,
	isLoading: true,
	refresh: false,
	isLoadingMore: false,
	canLoadMore: false
}

export const SET_POKEMONS_LIST = 'SET_POKEMONS_LIST'
export const MERGE_POKEMONS_LIST = 'MERGE_POKEMONS_LIST'
export const SET_POKEMONS_LOADING = 'SET_POKEMONS_LOADING'
export const ADD_POKEMONS_PAGE = 'ADD_POKEMONS_PAGE'

export const SET_POKEMONS_FILTER_ITEM = 'SET_POKEMONS_FILTER_ITEM'
export const TOGGLE_POKEMONS_REFRESH = 'TOGGLE_POKEMONS_REFRESH'

const reducer = (state = defaultState, action) => {
	switch (action.type) {
		case SET_POKEMONS_LIST:
			var { pokemons, total, totalPages, page } = action.payload
			return { ...state, list: pokemons, total, page: 1, isLoading: false, canLoadMore: total_pages > page }

		case MERGE_POKEMONS_LIST:
			var { pokemons, page, total, total_pages } = action.payload
			return { ...state, list: [...state.list, ...pokemons], isLoadingMore: false, canLoadMore: total_pages > page }

		case SET_POKEMONS_LOADING:
			return { ...state, isLoading: action.payload }

		case ADD_POKEMONS_PAGE:
			return { ...state, page: state.page + 1, isLoadingMore: true }

		case SET_POKEMONS_FILTER_ITEM:
			const { field, value } = action.payload
			return { ...state, filter: { ...state.filter, [field]: value } }

		case TOGGLE_POKEMONS_REFRESH:
			return { ...state, refresh: !state.refresh }

		default:
			return state
	}
}

export default reducer
