import {connect} from 'react-redux'
import React from 'react'

import {formatShortRegion} from '../../../helpers/address'
import Header from '../../../components/sctructure/Header'

const ShowPokemonPageHeader = ({pokemon}) => {
  const isReady = !!pokemon.id
  const title = isReady ? pokemon.name : '.......'
  const subtitle = isReady ? formatShortRegion(pokemon.address) : '...'
  const image = isReady && pokemon.default_image ? pokemon.default_image['520x280'] : '...'

  return <Header title={title} subtitle={subtitle} overlay bgImage={image} />
}

const mapState = state => ({
  pokemon: state.pokemon.single,
})

export default connect(mapState)(ShowPokemonPageHeader)
