import { connect } from 'react-redux'
import React from 'react'

import { MERGE_POKEMONS_LIST } from './pokemons.reducer'
import { SET_POKEMON_FAMILY_LIST, SET_POKEMON_FAMILY_LOADING } from './pokemonFamily.reducer'
import { pokemonsResource } from '../../../api/pokemons/pokemonsApi'
import { prepareFilterParams } from '../../../helpers/filter'
import { sd } from '../../../helpers/redux'
import Text from '../../../components/presentation/Text'

class ListPokemonsLoader extends React.Component {
	componentWillMount() {
		const { fetchData, familyID, familyMembers } = this.props
		if (!familyMembers.length) {
			fetchData(familyID)
		}
	}

	componentWillReceiveProps(nextProps) {
		const { familyID } = nextProps
		if (familyID !== this.props.familyID) {
			fetchData(familyID)
		}
	}

	render() {
		const { isLoading, children } = this.props
		if (!!isLoading) return <Text>Carregando...</Text>
		return children
	}
}

const mapState = state => ({
	familyMembers: state.pokemonFamily.list,
	isLoading: state.pokemonFamily.isLoading
})

const mapActions = dispatch => ({
	fetchData: familyID => {
		sd(dispatch, SET_POKEMON_FAMILY_LOADING, true)
		pokemonsResource
			.get(familyID)
			.then(data => {
				sd(dispatch, SET_POKEMON_FAMILY_LIST, data)
			})
			.catch(err => {
				sd(dispatch, SET_POKEMON_FAMILY_LOADING, false)
			})
	},

	fetchMoreData: (filter, page) => {
		pokemonsResource.getList({ queryParams: { page, ...prepareFilterParams(filter) } }).then(data => {
			console.log(data)
			sd(dispatch, MERGE_POKEMONS_LIST, data)
		})
	}
})

export default connect(mapState, mapActions)(ListPokemonsLoader)
