const defaultState = {
	list: [],
	isLoading: true
}

export const SET_POKEMON_FAMILY_LIST = 'SET_POKEMON_FAMILY_LIST'
export const SET_POKEMON_FAMILY_LOADING = 'SET_POKEMON_FAMILY_LOADING'

const reducer = (state = defaultState, action) => {
	switch (action.type) {
		case SET_POKEMON_FAMILY_LIST:
			var { list } = action.payload
			return { ...state, list, isLoading: false }

		case SET_POKEMON_FAMILY_LOADING:
			return { ...state, isLoading: action.payload }

		default:
			return state
	}
}

export default reducer
