import { Col, Image, Row } from 'react-bootstrap'
import { connect } from 'react-redux'
import React from 'react'

import { SET_POKEMON_SINGLE } from '../show/pokemon.reducer'
import { sd } from '../../../helpers/redux'
import { formatTo3Digits } from '../../../helpers/numbers'
import ShowPokemonFamilyLoader from './ShowPokemonFamilyLoader'

const ShowPokemonFamily = ({ familyID, familyMembers, goTo }) => (
	<ShowPokemonFamilyLoader familyID={familyID}>
		<Row>
			{familyMembers.map(pokemon => (
				<Col>
					<a onClick={() => goTo(pokemon)}>
						<Image
							roundedCircle
							src={`https://assets.pokemon.com/assets/cms2/img/pokedex/detail/${formatTo3Digits(pokemon.number)}.png`}
						/>
						{pokemon.name}
					</a>
				</Col>
			))}
		</Row>
	</ShowPokemonFamilyLoader>
)

const mapState = state => ({
	familyMembers: state.pokemonFamily.single.members || []
})

const mapActions = d => ({
	goTo(pokemon) {
		sd(d, SET_POKEMON_SINGLE, pokemon)
		d(push('pokemons/' + pokemon.id))
	}
})

export default connect(mapState, mapActions)(ShowPokemonFamily)
