import { connect } from 'react-redux'
import { push } from 'react-router-redux'
import React from 'react'

import { NUMBER_SELECT_OPTIONS } from '../../../config/const'
import { SET_POKEMONS_FILTER_ITEM, TOGGLE_POKEMONS_REFRESH } from './pokemons.reducer'
import { sd } from '../../../helpers/redux'
import Button from '../../../components/actions/Button'
import Col from '../../../components/grid/Col'
import Grid from '../../../components/grid/Grid'
import ListPokemonTypesLoader from '../../pokemonTypes/list/ListPokemonTypesLoader'
import Select from '../../../components/form/Select'
import SelectPokemonTypes from '../../pokemonTypes/list/SelectPokemonTypes'

const OtherInputs = ({ filter, onChange }) => (
	<div>
		<Grid noMarginTopBottom>
			<Col>
				<Select
					label="Suites"
					onChange={e => onChange('suites[]', e.target.value)}
					options={NUMBER_SELECT_OPTIONS}
					value={filter['suites[]']}
				/>
			</Col>

			<Col>
				<Select
					label="Vagas"
					onChange={e => onChange('parking[]', e.target.value)}
					options={NUMBER_SELECT_OPTIONS}
					value={filter['parking[]']}
				/>
			</Col>
		</Grid>
	</div>
)

class SearchPokemonsForm extends React.Component {
	constructor(props) {
		super(props)
		this.handleSubmit = this.handleSubmit.bind(this)
	}

	handleSubmit(e) {
		e.preventDefault()
		this.props.refreshPokemonsList()
	}

	render() {
		const { shortForm, filter, onChange } = this.props

		return (
			<form onSubmit={this.handleSubmit}>
				<Grid noMarginTopBottom>
					<Col></Col>

					<Col flex={2}></Col>
				</Grid>

				<ListPokemonTypesLoader>
					<SelectPokemonTypes
						label="Tipo de imóvel"
						onChange={e => onChange('type[]', e.target.value)}
						value={filter['type[]']}
					/>
				</ListPokemonTypesLoader>
				<Grid noMarginTopBottom>
					<Col>
						<Select
							label="Quartos"
							onChange={e => onChange('bedrooms[]', e.target.value)}
							options={NUMBER_SELECT_OPTIONS}
							value={filter['bedrooms[]']}
						/>
					</Col>

					<Col>
						<Select
							label="Banheiros"
							onChange={e => onChange('bathrooms[]', e.target.value)}
							options={NUMBER_SELECT_OPTIONS}
							value={filter['bathrooms[]']}
						/>
					</Col>
				</Grid>
				{!shortForm && <OtherInputs filter={filter} onChange={onChange} />}
				<Button>Buscar</Button>
			</form>
		)
	}
}

const mapState = state => ({
	filter: state.pokemons.filter
})

const mapActions = d => ({
	onChange(field, value) {
		sd(d, SET_POKEMONS_FILTER_ITEM, { field, value })
	},

	refreshPokemonsList() {
		d(push('/pokemons'))
		sd(d, TOGGLE_POKEMONS_REFRESH)
	}
})

export default connect(mapState, mapActions)(SearchPokemonsForm)
